package task4;

public class Javabasics {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	   // Declare and initialize four integer variables
		        int a = 10;
		        int b = 20;
		        int c = 15;
		        int d = 10;

		        // Calculate the sums
		        int sumAB = a + b;
		        int sumCD = c + d;

		        // Check if sum of a and b is greater than sum of c and d
		        if (sumAB > sumCD) {
		            System.out.println("The sum of a and b (" + sumAB + ") is greater than the sum of c and d (" + sumCD + ").");
		        } else {
		            System.out.println("The sum of a and b (" + sumAB + ") is not greater than the sum of c and d (" + sumCD + ").");
		        }
		        
	}
	
}
		    
		

	


